"use client";

import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";

import { Skeleton } from "@/components/ui/skeleton";
import { cn } from "@/lib/utils";
import { useCurrentUserImage } from "../hooks/use-current-user-image";
import { useCurrentUserName } from "../hooks/use-current-user-name";

interface CurrentUserAvatarProps {
  size?: "sm" | "md" | "lg";
  className?: string;
  showFallback?: boolean;
}

const sizeClasses = {
  sm: "h-8 w-8 text-xs",
  md: "h-10 w-10 text-sm",
  lg: "h-12 w-12 text-base",
};

function getInitials(name: string | null): string {
  if (!name) return "?";
  return name
    .split(" ")
    .map((n) => n[0])
    .join("")
    .toUpperCase()
    .slice(0, 2);
}

export function CurrentUserAvatar({
  size = "md",
  className,
  showFallback = true,
}: CurrentUserAvatarProps) {
  const image = useCurrentUserImage();
  const name = useCurrentUserName();

  // Loading state
  if (image === undefined || name === undefined) {
    return (
      <Skeleton className={cn("rounded-full", sizeClasses[size], className)} />
    );
  }

  // Not authenticated
  if (!showFallback && !image && !name) {
    return null;
  }

  return (
    <Avatar className={cn(sizeClasses[size], className)}>
      <AvatarImage src={image ?? undefined} alt={name ?? "User"} />
      <AvatarFallback>{getInitials(name)}</AvatarFallback>
    </Avatar>
  );
}

"use client";

import { useQuery } from "convex/react";
import { api } from "@/convex/_generated/api";

export function useCurrentUserImage() {
  const user = useQuery(api.users.current);
  return user?.image ?? null;
}

"use client";

import { useQuery } from "convex/react";
import { api } from "@/convex/_generated/api";

export function useCurrentUserName() {
  const user = useQuery(api.users.current);
  return user?.name ?? null;
}

import { defineSchema, defineTable } from "convex/server";
import { authTables } from "@convex-dev/auth/server";
import { v } from "convex/values";

/**
 * Schema for user avatar functionality.
 *
 * This schema requires authentication - the current-user-avatar component
 * displays the authenticated user's profile image and name.
 *
 * Note: This schema includes authTables which requires @convex-dev/auth.
 * If you're using a different auth system, you can remove authTables
 * and create your own users table.
 */
export default defineSchema({
  // Auth tables from @convex-dev/auth
  ...authTables,

  // Users table with profile information
  users: defineTable({
    // Profile fields
    name: v.optional(v.string()),
    email: v.optional(v.string()),
    image: v.optional(v.string()),

    // Auth metadata
    emailVerificationTime: v.optional(v.number()),
    isAnonymous: v.optional(v.boolean()),
  })
    .index("by_email", ["email"])
    .index("by_anonymous", ["isAnonymous"]),
});

import { query } from "./_generated/server";
import type { QueryCtx } from "./_generated/server";
import { v } from "convex/values";
import type { Id } from "./_generated/dataModel";
import { getAuthUserId } from "@convex-dev/auth/server";

/**
 * User validator for return types.
 */
const userValidator = v.object({
  _id: v.id("users"),
  _creationTime: v.number(),
  name: v.optional(v.string()),
  email: v.optional(v.string()),
  image: v.optional(v.string()),
  emailVerificationTime: v.optional(v.number()),
  isAnonymous: v.optional(v.boolean()),
});

/**
 * Get the current authenticated user's profile.
 *
 * This query is reactive - it automatically updates when user data changes.
 * Returns null if not authenticated.
 *
 * Security: Only returns data for the authenticated user themselves.
 * The user's own email is included since they are viewing their own profile.
 */
export const current = query({
  args: {},
  returns: v.union(userValidator, v.null()),
  handler: async (ctx: QueryCtx) => {
    const userId = await getAuthUserId(ctx);
    if (!userId) {
      return null;
    }
    return await ctx.db.get(userId);
  },
});

/**
 * Public profile validator - excludes sensitive fields like email.
 */
const publicProfileValidator = v.object({
  _id: v.id("users"),
  name: v.optional(v.string()),
  image: v.optional(v.string()),
});

/**
 * Get a user's public profile by ID.
 * Only returns safe, public fields (name, image).
 *
 * Security: Never exposes email or other sensitive data.
 */
export const get = query({
  args: { userId: v.id("users") },
  returns: v.union(publicProfileValidator, v.null()),
  handler: async (ctx: QueryCtx, args: { userId: Id<"users"> }) => {
    const user = await ctx.db.get(args.userId);
    if (!user) {
      return null;
    }
    // Only return public fields
    return {
      _id: user._id,
      name: user.name,
      image: user.image,
    };
  },
});

import { ConvexReactClient } from "convex/react";

const convexUrl = (import.meta as any).env.VITE_CONVEX_URL as string;

export const convex = new ConvexReactClient(convexUrl);

import { ConvexAuthProvider } from "@convex-dev/auth/react";
import { ConvexReactClient } from "convex/react";
import { ReactNode } from "react";

const convex = new ConvexReactClient(
  (import.meta as any).env.VITE_CONVEX_URL as string,
);

export function ConvexClientProvider({ children }: { children: ReactNode }) {
  return <ConvexAuthProvider client={convex}>{children}</ConvexAuthProvider>;
}
