}

export function LogoutButton({
  showIcon = true,
  children,
  ...props
}: LogoutButtonProps) {
  const { signOut } = useAuthActions();

  return (
    <Button onClick={() => signOut()} {...props}>
      {showIcon && <LogOut className="mr-2 h-4 w-4" />}
      {children ?? "Sign out"}
    </Button>
  );
}

