            </Avatar>
            <div>
              <p className="font-semibold">{user.name ?? "Anonymous"}</p>
              <p className="text-sm text-muted-foreground">{user.email}</p>
            </div>
          </div>
          <LogoutButton variant="outline" className="w-full" />
        </CardContent>
      </Card>
    </div>
  );
}

import { defineSchema, defineTable } from "convex/server";
import { authTables } from "@convex-dev/auth/server";
import { v } from "convex/values";

/**
 * Schema for password-based authentication.
 * Includes the users table and all required auth tables from @convex-dev/auth.
 *
 * Security considerations:
 * - Email is indexed for fast lookups but never exposed in public queries
 * - emailVerificationTime tracks when email was verified
 * - isAnonymous flag supports gradual account upgrades
 */
export default defineSchema({
  // Auth tables from @convex-dev/auth (authAccounts, authSessions, authVerificationCodes, etc.)
  ...authTables,

  // Users table with profile information
  users: defineTable({
    // Profile fields
    name: v.optional(v.string()),
    email: v.optional(v.string()),
    image: v.optional(v.string()),

    // Auth metadata
    emailVerificationTime: v.optional(v.number()),
    isAnonymous: v.optional(v.boolean()),
  })
    .index("by_email", ["email"])
    .index("by_anonymous", ["isAnonymous"]),
});

import { convexAuth } from "@convex-dev/auth/server";
import { Password } from "@convex-dev/auth/providers/Password";
import Resend from "@auth/core/providers/resend";
import GitHub from "@auth/core/providers/github";
import Google from "@auth/core/providers/google";
import { DataModel } from "./_generated/dataModel";

/**
 * Custom Resend provider for email verification OTP.
 *
 * Sends an 8-digit OTP code for email verification during sign-up.
 * Configure AUTH_RESEND_KEY environment variable to enable.
 */
const ResendOTP = Resend({
  id: "resend-otp-verification",
  apiKey: process.env.AUTH_RESEND_KEY,
  async generateVerificationToken() {
    const code = Array.from({ length: 8 }, () =>
      Math.floor(Math.random() * 10),
    ).join("");
    return code;
  },
  async sendVerificationRequest({ identifier: email, provider, token }) {
    const response = await fetch("https://api.resend.com/emails", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${provider.apiKey}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        from: process.env.AUTH_EMAIL_FROM ?? "onboarding@resend.dev",
        to: [email],
        subject: "Verify your email",
        text: `Your verification code is: ${token}\n\nThis code expires in 15 minutes.`,
      }),
    });

    if (!response.ok) {
      throw new Error("Could not send verification email");
    }
  },
});

/**
 * Custom Resend provider for password reset OTP.
 *
 * Sends an 8-digit OTP code for password reset.
 * Configure AUTH_RESEND_KEY environment variable to enable.
 */
const ResendOTPPasswordReset = Resend({
  id: "resend-otp-reset",
  apiKey: process.env.AUTH_RESEND_KEY,
  async generateVerificationToken() {
    const code = Array.from({ length: 8 }, () =>
      Math.floor(Math.random() * 10),
    ).join("");
    return code;
  },
  async sendVerificationRequest({ identifier: email, provider, token }) {
    const response = await fetch("https://api.resend.com/emails", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${provider.apiKey}`,
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        from: process.env.AUTH_EMAIL_FROM ?? "onboarding@resend.dev",
        to: [email],
        subject: "Reset your password",
        text: `Your password reset code is: ${token}\n\nThis code expires in 15 minutes.`,
      }),
    });

    if (!response.ok) {
      throw new Error("Could not send password reset email");
    }
  },
});

/**
 * Custom Password provider with:
 * - Email verification during sign-up (via ResendOTP)
 * - Password reset flow (via ResendOTPPasswordReset)
 * - Strong password requirements
 */
const CustomPassword = Password<DataModel>({
  // Require email verification during sign-up
  verify: ResendOTP,

  // Enable password reset via email
  reset: ResendOTPPasswordReset,

  // Custom password validation
  validatePasswordRequirements: (password: string) => {
    if (password.length < 8) {
      throw new Error("Password must be at least 8 characters");
    }
    if (!/[a-z]/.test(password)) {
      throw new Error("Password must contain at least one lowercase letter");
    }
    if (!/[A-Z]/.test(password)) {
      throw new Error("Password must contain at least one uppercase letter");
    }
    if (!/[0-9]/.test(password)) {
      throw new Error("Password must contain at least one number");
    }
  },

  // Customize profile to handle additional user fields
  profile(params) {
    return {
      email: params.email as string,
      name: (params.name as string) || undefined,
    };
  },
});

// Build providers list dynamically based on available env vars
const providers: Parameters<typeof convexAuth>[0]["providers"] = [
  CustomPassword,
];

// Only add GitHub if credentials are configured
if (process.env.AUTH_GITHUB_ID && process.env.AUTH_GITHUB_SECRET) {
  providers.push(GitHub);
}

// Only add Google if credentials are configured
if (process.env.AUTH_GOOGLE_ID && process.env.AUTH_GOOGLE_SECRET) {
  providers.push(Google);
}

/**
 * Convex Auth configuration.
 *
 * Features:
 * - Password authentication with email verification
 * - Password reset via OTP codes
 * - Strong password requirements
 * - Optional GitHub OAuth (if AUTH_GITHUB_ID/SECRET configured)
 * - Optional Google OAuth (if AUTH_GOOGLE_ID/SECRET configured)
 *
 * Environment variables:
 * - AUTH_RESEND_KEY: Resend API key for sending emails (required)
 * - AUTH_EMAIL_FROM: Sender email address (optional)
 * - AUTH_GITHUB_ID, AUTH_GITHUB_SECRET: GitHub OAuth (optional)
 * - AUTH_GOOGLE_ID, AUTH_GOOGLE_SECRET: Google OAuth (optional)
 */
export const { auth, signIn, signOut, store, isAuthenticated } = convexAuth({
  providers,
});

import { query, mutation } from "./_generated/server";
import { v } from "convex/values";
import { getAuthUserId } from "@convex-dev/auth/server";
import { ConvexError } from "convex/values";

/**
 * User validator for internal use.
 * Includes all fields - use publicProfileValidator for external queries.
 */
const userValidator = v.object({
  _id: v.id("users"),
  _creationTime: v.number(),
  name: v.optional(v.string()),
  email: v.optional(v.string()),
  image: v.optional(v.string()),
  emailVerificationTime: v.optional(v.number()),
  isAnonymous: v.optional(v.boolean()),
});

/**
 * Public profile validator - excludes sensitive fields like email.
 * Use this when returning user data to other users.
 */
const publicProfileValidator = v.object({
  _id: v.id("users"),
  name: v.optional(v.string()),
  image: v.optional(v.string()),
});

/**
 * Get the current authenticated user's full profile.
 * Returns null if not authenticated.
 *
 * Security: Only returns data for the authenticated user themselves.
 */
export const current = query({
  args: {},
  returns: v.union(userValidator, v.null()),
  handler: async (ctx: any) => {
    const userId = await getAuthUserId(ctx);
    if (!userId) {
      return null;
    }
    return await ctx.db.get(userId);
  },
});

/**
 * Get a user's public profile by ID.
 * Only returns safe, public fields (name, image).
 *
 * Security: Never exposes email or other sensitive data.
 */
export const get = query({
  args: { userId: v.id("users") },
  returns: v.union(publicProfileValidator, v.null()),
  handler: async (ctx: any, args: any) => {
    const user = await ctx.db.get(args.userId);
    if (!user) {
      return null;
    }
    // Only return public fields
    return {
      _id: user._id,
      name: user.name,
      image: user.image,
    };
  },
});

/**
 * Update the current user's profile.
 * Only allows updating safe fields (name, image).
 *
 * Security:
 * - Requires authentication
 * - Only allows updating own profile
 * - Validates input types
 */
export const updateProfile = mutation({
  args: {
    name: v.optional(v.string()),
    image: v.optional(v.string()),
  },
  returns: v.null(),
  handler: async (ctx: any, args: any) => {
    const userId = await getAuthUserId(ctx);
    if (!userId) {
      throw new ConvexError({
        code: "UNAUTHORIZED",
        message: "You must be logged in to update your profile",
      });
    }

    // Build updates object with only provided fields
    const updates: any = {};
    if (args.name !== undefined) {
      // Sanitize name - trim whitespace, limit length
      const sanitizedName = args.name.trim().slice(0, 100);
      if (sanitizedName) {
        updates.name = sanitizedName;
      }
    }
    if (args.image !== undefined) {
      // Validate image URL format
      if (args.image && !isValidUrl(args.image)) {
        throw new ConvexError({
          code: "INVALID_INPUT",
          message: "Invalid image URL",
        });
      }
      updates.image = args.image;
    }

    if (Object.keys(updates).length > 0) {
      await ctx.db.patch(userId, updates);
    }

    return null;
  },
});

/**
 * Helper to validate URL format.
 */
function isValidUrl(string: string): boolean {
  try {
    const url = new URL(string);
    return url.protocol === "http:" || url.protocol === "https:";
  } catch {
    return false;
  }
}

