"use client";

import { Alert, AlertDescription } from "@/components/ui/alert";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useAuthActions } from "@convex-dev/auth/react";
import { Loader2 } from "lucide-react";
import { useState } from "react";

interface LoginFormProps {
  onSignUpClick?: () => void;
  onForgotPasswordClick?: () => void;
}

export function LoginForm({
  onSignUpClick,
  onForgotPasswordClick,
}: LoginFormProps) {
  const { signIn } = useAuthActions();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    try {
      const formData = new FormData();
      formData.set("email", email);
      formData.set("password", password);
      formData.set("flow", "signIn");

      await signIn("password", formData);
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to sign in");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Card className="w-full max-w-md shadow-lg">
      <CardHeader className="space-y-1 pb-4">
        <CardTitle className="text-2xl">Welcome back</CardTitle>
        <CardDescription>Sign in to your account to continue</CardDescription>
      </CardHeader>
      <form onSubmit={handleSubmit}>
        <CardContent className="space-y-5">
          {error && (
            <Alert
              variant="destructive"
              className="animate-in fade-in-0 slide-in-from-top-1 duration-300"
            >
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}
          <div className="space-y-2">
            <Label htmlFor="email" className="text-sm font-semibold">
              Email
            </Label>
            <Input
              id="email"
              type="email"
              placeholder="you@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              disabled={loading}
              className="h-11"
            />
          </div>
          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <Label htmlFor="password" className="text-sm font-semibold">
                Password
              </Label>
              {onForgotPasswordClick && (
                <button
                  type="button"
                  onClick={onForgotPasswordClick}
                  className="text-xs font-medium text-primary hover:underline transition-colors"
                >
                  Forgot password?
                </button>
              )}
            </div>
            <Input
              id="password"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              disabled={loading}
              className="h-11"
            />
          </div>
        </CardContent>
        <CardFooter className="flex flex-col gap-4 pt-2">
          <Button type="submit" size="lg" className="w-full" disabled={loading}>
            {loading && <Loader2 className="mr-2 h-5 w-5 animate-spin" />}
            Sign in
          </Button>
          {onSignUpClick && (
            <>
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <span className="w-full border-t" />
                </div>
                <div className="relative flex justify-center text-xs uppercase">
                  <span className="bg-card px-2 text-muted-foreground">or</span>
                </div>
              </div>
              <p className="text-sm text-center text-muted-foreground">
                Don't have an account?{" "}
                <button
                  type="button"
                  onClick={onSignUpClick}
                  className="font-semibold text-primary hover:underline transition-colors"
                >
                  Sign up
                </button>
              </p>
            </>
          )}
        </CardFooter>
      </form>
    </Card>
  );
}

"use client";

import { Alert, AlertDescription } from "@/components/ui/alert";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";

import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useAuthActions } from "@convex-dev/auth/react";
import { Loader2 } from "lucide-react";
import { useState } from "react";

interface SignUpFormProps {
  onLoginClick?: () => void;
  onSuccess?: () => void;
}

export function SignUpForm({ onLoginClick, onSuccess }: SignUpFormProps) {
  const { signIn } = useAuthActions();
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError(null);

    if (password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    if (password.length < 8) {
      setError("Password must be at least 8 characters");
      return;
    }

    setLoading(true);

    try {
      const formData = new FormData();
      formData.set("name", name);
      formData.set("email", email);
      formData.set("password", password);
      formData.set("flow", "signUp");

      await signIn("password", formData);
      onSuccess?.();
    } catch (err) {
      setError(err instanceof Error ? err.message : "Failed to create account");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Card className="w-full max-w-md shadow-lg">
      <CardHeader className="space-y-1 pb-4">
        <CardTitle className="text-2xl">Create an account</CardTitle>
        <CardDescription>Enter your details to get started</CardDescription>
      </CardHeader>
      <form onSubmit={handleSubmit}>
        <CardContent className="space-y-4">
          {error && (
            <Alert
              variant="destructive"
              className="animate-in fade-in-0 slide-in-from-top-1 duration-300"
            >
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}
          <div className="space-y-2">
            <Label htmlFor="name" className="text-sm font-semibold">
              Name
            </Label>
            <Input
              id="name"
              type="text"
              placeholder="John Doe"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
              disabled={loading}
              className="h-11"
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="email" className="text-sm font-semibold">
              Email
            </Label>
            <Input
              id="email"
              type="email"
              placeholder="you@example.com"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
              disabled={loading}
              className="h-11"
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="password" className="text-sm font-semibold">
              Password
            </Label>
            <Input
              id="password"
              type="password"
              placeholder="At least 8 characters"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
              disabled={loading}
              className="h-11"
            />
          </div>
          <div className="space-y-2">
            <Label htmlFor="confirmPassword" className="text-sm font-semibold">
              Confirm Password
            </Label>
            <Input
              id="confirmPassword"
              type="password"
              placeholder="Re-enter your password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
              disabled={loading}
              className="h-11"
            />
          </div>
        </CardContent>
        <CardFooter className="flex flex-col gap-4 pt-2">
          <Button type="submit" size="lg" className="w-full" disabled={loading}>
            {loading && <Loader2 className="mr-2 h-5 w-5 animate-spin" />}
            Create account
          </Button>
          {onLoginClick && (
            <>
              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <span className="w-full border-t" />
                </div>
                <div className="relative flex justify-center text-xs uppercase">
                  <span className="bg-card px-2 text-muted-foreground">or</span>
                </div>
              </div>
              <p className="text-sm text-center text-muted-foreground">
                Already have an account?{" "}
                <button
                  type="button"
                  onClick={onLoginClick}
                  className="font-semibold text-primary hover:underline transition-colors"
                >
                  Sign in
                </button>
              </p>
            </>
          )}
        </CardFooter>
      </form>
    </Card>
  );
}

import { defineSchema, defineTable } from "convex/server";
import { authTables } from "@convex-dev/auth/server";
import { v } from "convex/values";

/**
 * Schema for password-based authentication.
 * Includes the users table and all required auth tables from @convex-dev/auth.
 *
 * Security considerations:
 * - Email is indexed for fast lookups but never exposed in public queries
 * - emailVerificationTime tracks when email was verified
 * - isAnonymous flag supports gradual account upgrades
 */
export default defineSchema({
  // Auth tables from @convex-dev/auth (authAccounts, authSessions, authVerificationCodes, etc.)
  ...authTables,

  // Users table with profile information
  users: defineTable({
    // Profile fields
    name: v.optional(v.string()),
    email: v.optional(v.string()),
    image: v.optional(v.string()),

    // Auth metadata
    emailVerificationTime: v.optional(v.number()),
    isAnonymous: v.optional(v.boolean()),
  })
    .index("by_email", ["email"])
    .index("by_anonymous", ["isAnonymous"]),
});

import { convexAuth } from "@convex-dev/auth/server";
import { Password } from "@convex-dev/auth/providers/Password";

/**
 * Convex Auth configuration for password-based authentication.
 *
 * Security features:
 * - Password provider with built-in hashing (bcrypt)
 * - Email verification support
 * - Password reset flow
 * - Secure session management
 *
 * To enable email verification, configure RESEND_API_KEY or another email provider.
 */
export const { auth, signIn, signOut, store } = convexAuth({
  providers: [Password],
  callbacks: {
    /**
     * Called when a user signs in or signs up.
     * Creates the user record if it doesn't exist.
     */
    async createOrUpdateUser(ctx: any, args: any) {
      if (args.existingUserId) {
        // Existing user - optionally update profile from provider
        const user = await ctx.db.get(args.existingUserId);
        if (user && args.profile) {
          // Update name/image if provided by OAuth (not applicable for Password)
          const updates: any = {};
          if (args.profile.name && !user.name) {
            updates.name = args.profile.name;
          }
          if (args.profile.image && !user.image) {
            updates.image = args.profile.image;
          }
          if (Object.keys(updates).length > 0) {
            await ctx.db.patch(args.existingUserId, updates);
          }
        }
        return args.existingUserId;
      }

      // Create new user
      const userId = await ctx.db.insert("users", {
        name: args.profile?.name,
        email: args.profile?.email,
        image: args.profile?.image,
        emailVerificationTime: args.profile?.emailVerified
          ? Date.now()
          : undefined,
        isAnonymous: false,
      });

      return userId;
    },
  },
});

/**
 * Auth configuration for Convex Auth.
 *
 * The domain should match your Convex deployment URL.
 * This is automatically set via CONVEX_SITE_URL environment variable.
 */
export default {
  providers: [
    {
      domain: process.env.CONVEX_SITE_URL,
      applicationID: "convex",
    },
  ],
};

import { query, mutation } from "./_generated/server";
import { v } from "convex/values";
import { getAuthUserId } from "@convex-dev/auth/server";
import { ConvexError } from "convex/values";

/**
 * User validator for internal use.
 * Includes all fields - use publicProfileValidator for external queries.
 */
const userValidator = v.object({
  _id: v.id("users"),
  _creationTime: v.number(),
  name: v.optional(v.string()),
  email: v.optional(v.string()),
  image: v.optional(v.string()),
  emailVerificationTime: v.optional(v.number()),
  isAnonymous: v.optional(v.boolean()),
});

/**
 * Public profile validator - excludes sensitive fields like email.
 * Use this when returning user data to other users.
 */
const publicProfileValidator = v.object({
  _id: v.id("users"),
  name: v.optional(v.string()),
  image: v.optional(v.string()),
});

/**
 * Get the current authenticated user's full profile.
 * Returns null if not authenticated.
 *
 * Security: Only returns data for the authenticated user themselves.
 */
export const current = query({
  args: {},
  returns: v.union(userValidator, v.null()),
  handler: async (ctx: any) => {
    const userId = await getAuthUserId(ctx);
    if (!userId) {
      return null;
    }
    return await ctx.db.get(userId);
  },
});

/**
 * Get a user's public profile by ID.
 * Only returns safe, public fields (name, image).
 *
 * Security: Never exposes email or other sensitive data.
 */
export const get = query({
  args: { userId: v.id("users") },
  returns: v.union(publicProfileValidator, v.null()),
  handler: async (ctx: any, args: any) => {
    const user = await ctx.db.get(args.userId);
    if (!user) {
      return null;
    }
    // Only return public fields
    return {
      _id: user._id,
      name: user.name,
      image: user.image,
    };
  },
});

/**
 * Update the current user's profile.
 * Only allows updating safe fields (name, image).
 *
 * Security:
 * - Requires authentication
 * - Only allows updating own profile
 * - Validates input types
 */
export const updateProfile = mutation({
  args: {
    name: v.optional(v.string()),
    image: v.optional(v.string()),
  },
  returns: v.null(),
  handler: async (ctx: any, args: any) => {
    const userId = await getAuthUserId(ctx);
    if (!userId) {
      throw new ConvexError({
        code: "UNAUTHORIZED",
        message: "You must be logged in to update your profile",
      });
    }

    // Build updates object with only provided fields
    const updates: any = {};
    if (args.name !== undefined) {
      // Sanitize name - trim whitespace, limit length
      const sanitizedName = args.name.trim().slice(0, 100);
      if (sanitizedName) {
        updates.name = sanitizedName;
      }
    }
    if (args.image !== undefined) {
      // Validate image URL format
      if (args.image && !isValidUrl(args.image)) {
        throw new ConvexError({
          code: "INVALID_INPUT",
          message: "Invalid image URL",
        });
      }
      updates.image = args.image;
    }

    if (Object.keys(updates).length > 0) {
      await ctx.db.patch(userId, updates);
    }

    return null;
  },
});

/**
 * Helper to validate URL format.
 */
function isValidUrl(string: string): boolean {
  try {
    const url = new URL(string);
    return url.protocol === "http:" || url.protocol === "https:";
  } catch {
    return false;
  }
}

import { httpRouter } from "convex/server";
import { auth } from "./auth";

/**
 * HTTP router for Convex Auth.
 *
 * This sets up the required HTTP endpoints for authentication:
 * - POST /api/auth/signin - Sign in
 * - POST /api/auth/signout - Sign out
 * - GET /api/auth/signin/* - OAuth callbacks
 * - POST /api/auth/callback/* - OAuth callbacks
 *
 * These routes are automatically configured by auth.addHttpRoutes().
 */
const http = httpRouter();

// Add all auth HTTP routes
auth.addHttpRoutes(http);

export default http;

import { ConvexReactClient } from "convex/react";

const convexUrl = (import.meta as any).env.VITE_CONVEX_URL as string;

export const convex = new ConvexReactClient(convexUrl);

import { ConvexAuthProvider } from "@convex-dev/auth/react";
import { ConvexReactClient } from "convex/react";
import { ReactNode } from "react";

const convex = new ConvexReactClient(
  (import.meta as any).env.VITE_CONVEX_URL as string,
);

export function ConvexClientProvider({ children }: { children: ReactNode }) {
  return <ConvexAuthProvider client={convex}>{children}</ConvexAuthProvider>;
}
