  return (
    <svg className={className} viewBox="0 0 24 24" fill="currentColor">
      <path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z" />
    </svg>
  );
}

function GoogleIcon({ className }: { className?: string }) {
  return (
    <svg className={className} viewBox="0 0 24 24" fill="currentColor">
      <path d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z" />
      <path d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z" />
      <path d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z" />
      <path d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z" />
    </svg>
  );
}

type Provider = "github" | "google";

interface SocialLoginFormProps {
  providers?: Provider[];
}

export function SocialLoginForm({
  providers = ["github", "google"],
}: SocialLoginFormProps) {
  const { signIn } = useAuthActions();
  const [error, setError] = useState<string | null>(null);
  const [loadingProvider, setLoadingProvider] = useState<Provider | null>(null);

  const handleSocialLogin = async (provider: Provider) => {
    setError(null);
    setLoadingProvider(provider);

    try {
      await signIn(provider);
    } catch (err) {
      setError(
        err instanceof Error
          ? err.message
          : `Failed to sign in with ${provider}`,
      );
      setLoadingProvider(null);
    }
  };

  const providerConfig = {
    github: {
      name: "GitHub",
      icon: GitHubIcon,
    },
    google: {
      name: "Google",
      icon: GoogleIcon,
    },
  };

  return (
    <Card className="w-full max-w-md">
      <CardHeader>
        <CardTitle>Welcome</CardTitle>
        <CardDescription>Sign in with your social account</CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        {error && (
          <Alert variant="destructive">
            <AlertDescription>{error}</AlertDescription>
          </Alert>
        )}
        <div className="grid gap-3">
          {providers.map((provider) => {
            const config = providerConfig[provider];
            const Icon = config.icon;
            const isLoading = loadingProvider === provider;

            return (
              <Button
                key={provider}
                variant="outline"
                className="w-full"
                onClick={() => handleSocialLogin(provider)}
                disabled={loadingProvider !== null}
              >
                {isLoading ? (
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                ) : (
                  <Icon className="mr-2 h-4 w-4" />
                )}
                Continue with {config.name}
              </Button>
            );
          })}
        </div>
      </CardContent>
    </Card>
  );
}

"use client";

import { Button, type ButtonProps } from "@/components/ui/button";
import { useAuthActions } from "@convex-dev/auth/react";
import { LogOut } from "lucide-react";

interface LogoutButtonProps extends ButtonProps {
  showIcon?: boolean;
}

export function LogoutButton({
  showIcon = true,
  children,
  ...props
}: LogoutButtonProps) {
  const { signOut } = useAuthActions();

  return (
    <Button onClick={() => signOut()} {...props}>
      {showIcon && <LogOut className="mr-2 h-4 w-4" />}
      {children ?? "Sign out"}
    </Button>
  );
}

import { SocialLoginForm } from "@/components/login-form";

export default function LoginPage() {
  return (
    <div className="flex min-h-screen items-center justify-center p-4">
      <SocialLoginForm />
    </div>
  );
}

"use client";

import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";

import { LogoutButton } from "@/components/logout-button";
import { Skeleton } from "@/components/ui/skeleton";
import { api } from "@/convex/_generated/api";
import { useQuery } from "convex/react";

export default function ProtectedPage() {
  const user = useQuery(api.users.current);

  if (user === undefined) {
    return (
      <div className="flex min-h-screen items-center justify-center p-4">
        <Card className="w-full max-w-md">
          <CardHeader>
            <Skeleton className="h-8 w-48" />
            <Skeleton className="h-4 w-64" />
          </CardHeader>
          <CardContent>
            <Skeleton className="h-20 w-full" />
          </CardContent>
        </Card>
      </div>
    );
  }

  if (!user) {
    return (
      <div className="flex min-h-screen items-center justify-center p-4">
        <Card className="w-full max-w-md">
          <CardHeader>
            <CardTitle>Not authenticated</CardTitle>
            <CardDescription>Please sign in to view this page.</CardDescription>
          </CardHeader>
        </Card>
      </div>
    );
  }

  return (
    <div className="flex min-h-screen items-center justify-center p-4">
      <Card className="w-full max-w-md">
        <CardHeader>
          <CardTitle>Protected Page</CardTitle>
          <CardDescription>
            You are signed in and can view this content.
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex items-center gap-4">
            <Avatar className="h-16 w-16">
              <AvatarImage src={user.image} alt={user.name ?? "User"} />
              <AvatarFallback>
                {(user.name ?? "U").slice(0, 2).toUpperCase()}
              </AvatarFallback>
            </Avatar>
            <div>
              <p className="font-semibold">{user.name ?? "Anonymous"}</p>
              <p className="text-sm text-muted-foreground">{user.email}</p>
            </div>
          </div>
          <LogoutButton variant="outline" className="w-full" />
        </CardContent>
      </Card>
    </div>
  );
}

import { defineSchema, defineTable } from "convex/server";
import { authTables } from "@convex-dev/auth/server";
import { v } from "convex/values";

/**
 * Schema for social (OAuth) authentication.
 * Includes the users table and all required auth tables from @convex-dev/auth.
 *
 * Security considerations:
 * - Email is indexed for fast lookups but never exposed in public queries
 * - emailVerificationTime is set when OAuth provider verifies email
 * - Profile data (name, image) is populated from OAuth provider
 */
export default defineSchema({
  // Auth tables from @convex-dev/auth (authAccounts, authSessions, etc.)
  ...authTables,

  // Users table with profile information from OAuth providers
  users: defineTable({
    // Profile fields (populated from OAuth provider)
    name: v.optional(v.string()),
    email: v.optional(v.string()),
    image: v.optional(v.string()),

    // Auth metadata
    emailVerificationTime: v.optional(v.number()),
    isAnonymous: v.optional(v.boolean()),
  })
    .index("by_email", ["email"])
    .index("by_anonymous", ["isAnonymous"]),
});

import { convexAuth } from "@convex-dev/auth/server";
import GitHub from "@auth/core/providers/github";
import Google from "@auth/core/providers/google";

/**
 * Convex Auth configuration for social (OAuth) authentication.
 *
 * Supported providers:
 * - GitHub: Requires AUTH_GITHUB_ID and AUTH_GITHUB_SECRET
 * - Google: Requires AUTH_GOOGLE_ID and AUTH_GOOGLE_SECRET
 *
 * Security features:
 * - OAuth 2.0 with PKCE
 * - Automatic email verification from trusted providers
 * - Secure session management
 *
 * To add more providers, import them from @auth/core/providers and add to the array.
 */
export const { auth, signIn, signOut, store } = convexAuth({
  providers: [GitHub, Google],
  callbacks: {
    /**
     * Called when a user signs in via OAuth.
     * Creates the user record if it doesn't exist, or updates profile data.
     */
    async createOrUpdateUser(ctx: any, args: any) {
      if (args.existingUserId) {
        // Existing user - update profile from OAuth provider if needed
        const user = await ctx.db.get(args.existingUserId);
        if (user && args.profile) {
          const updates: any = {};

          // Update profile fields if provider has newer data
          if (args.profile.name && args.profile.name !== user.name) {
            updates.name = args.profile.name;
          }
          if (args.profile.image && args.profile.image !== user.image) {
            updates.image = args.profile.image;
          }
          // Mark email as verified if provider verifies it
          if (args.profile.emailVerified && !user.emailVerificationTime) {
            updates.emailVerificationTime = Date.now();
          }

          if (Object.keys(updates).length > 0) {
            await ctx.db.patch(args.existingUserId, updates);
          }
        }
        return args.existingUserId;
      }

      // Create new user with profile from OAuth provider
      const userId = await ctx.db.insert("users", {
        name: args.profile?.name,
        email: args.profile?.email,
        image: args.profile?.image,
        // OAuth providers that verify email set emailVerified to true
        emailVerificationTime: args.profile?.emailVerified
          ? Date.now()
          : undefined,
        isAnonymous: false,
      });

      return userId;
    },
  },
});

/**
 * Auth configuration for Convex Auth with OAuth providers.
 *
 * The domain should match your Convex deployment URL.
 * This is automatically set via CONVEX_SITE_URL environment variable.
 *
 * For OAuth providers, you also need to set:
 * - GitHub: AUTH_GITHUB_ID, AUTH_GITHUB_SECRET
 * - Google: AUTH_GOOGLE_ID, AUTH_GOOGLE_SECRET
 *
 * Configure the OAuth callback URLs in your provider's settings:
 * - GitHub: https://YOUR_CONVEX_URL/api/auth/callback/github
 * - Google: https://YOUR_CONVEX_URL/api/auth/callback/google
 */
export default {
  providers: [
    {
      domain: process.env.CONVEX_SITE_URL,
      applicationID: "convex",
    },
  ],
};

import { query, mutation } from "./_generated/server";
import { v } from "convex/values";
import { getAuthUserId } from "@convex-dev/auth/server";
import { ConvexError } from "convex/values";

/**
 * User validator for internal use.
 * Includes all fields - use publicProfileValidator for external queries.
 */
const userValidator = v.object({
  _id: v.id("users"),
  _creationTime: v.number(),
  name: v.optional(v.string()),
  email: v.optional(v.string()),
  image: v.optional(v.string()),
  emailVerificationTime: v.optional(v.number()),
  isAnonymous: v.optional(v.boolean()),
});

/**
 * Public profile validator - excludes sensitive fields like email.
 * Use this when returning user data to other users.
 */
const publicProfileValidator = v.object({
  _id: v.id("users"),
  name: v.optional(v.string()),
  image: v.optional(v.string()),
});

/**
 * Get the current authenticated user's full profile.
 * Returns null if not authenticated.
 *
 * Security: Only returns data for the authenticated user themselves.
 */
export const current = query({
  args: {},
  returns: v.union(userValidator, v.null()),
  handler: async (ctx: any) => {
    const userId = await getAuthUserId(ctx);
    if (!userId) {
      return null;
    }
    return await ctx.db.get(userId);
  },
});

/**
 * Get a user's public profile by ID.
 * Only returns safe, public fields (name, image).
 *
 * Security: Never exposes email or other sensitive data.
 */
export const get = query({
  args: { userId: v.id("users") },
  returns: v.union(publicProfileValidator, v.null()),
  handler: async (ctx: any, args: any) => {
    const user = await ctx.db.get(args.userId);
    if (!user) {
      return null;
    }
    // Only return public fields
    return {
      _id: user._id,
      name: user.name,
      image: user.image,
    };
  },
});

/**
 * Update the current user's profile.
 * Only allows updating safe fields (name, image).
 *
 * Security:
 * - Requires authentication
 * - Only allows updating own profile
 * - Validates input types
 */
export const updateProfile = mutation({
  args: {
    name: v.optional(v.string()),
    image: v.optional(v.string()),
  },
  returns: v.null(),
  handler: async (ctx: any, args: any) => {
    const userId = await getAuthUserId(ctx);
    if (!userId) {
      throw new ConvexError({
        code: "UNAUTHORIZED",
        message: "You must be logged in to update your profile",
      });
    }

    // Build updates object with only provided fields
    const updates: any = {};
    if (args.name !== undefined) {
      // Sanitize name - trim whitespace, limit length
      const sanitizedName = args.name.trim().slice(0, 100);
      if (sanitizedName) {
        updates.name = sanitizedName;
      }
    }
    if (args.image !== undefined) {
      // Validate image URL format
      if (args.image && !isValidUrl(args.image)) {
        throw new ConvexError({
          code: "INVALID_INPUT",
          message: "Invalid image URL",
        });
      }
      updates.image = args.image;
    }

    if (Object.keys(updates).length > 0) {
      await ctx.db.patch(userId, updates);
    }

    return null;
  },
});

/**
 * Helper to validate URL format.
 */
function isValidUrl(string: string): boolean {
  try {
    const url = new URL(string);
    return url.protocol === "http:" || url.protocol === "https:";
  } catch {
    return false;
  }
}

import { httpRouter } from "convex/server";
import { auth } from "./auth";

/**
 * HTTP router for Convex Auth with OAuth providers.
 *
 * This sets up the required HTTP endpoints for authentication:
 * - GET /api/auth/signin/github - Initiate GitHub OAuth
 * - GET /api/auth/signin/google - Initiate Google OAuth
 * - GET /api/auth/callback/github - GitHub OAuth callback
 * - GET /api/auth/callback/google - Google OAuth callback
 * - POST /api/auth/signout - Sign out
 *
 * These routes are automatically configured by auth.addHttpRoutes().
 */
const http = httpRouter();

// Add all auth HTTP routes
auth.addHttpRoutes(http);

export default http;

"use client";

import { ConvexReactClient } from "convex/react";

const convexUrl = process.env.NEXT_PUBLIC_CONVEX_URL!;

export const convex = new ConvexReactClient(convexUrl);

"use client";

import { ConvexAuthNextjsProvider } from "@convex-dev/auth/nextjs";
import { ConvexReactClient } from "convex/react";
import { ReactNode } from "react";

const convex = new ConvexReactClient(process.env.NEXT_PUBLIC_CONVEX_URL!);

export function ConvexClientProvider({ children }: { children: ReactNode }) {
  return (
    <ConvexAuthNextjsProvider client={convex}>
      {children}
    </ConvexAuthNextjsProvider>
  );
}

import {
  fetchQuery,
  fetchMutation,
  fetchAction,
  preloadQuery,
} from "convex/nextjs";
import { api } from "@/convex/_generated/api";

export { fetchQuery, fetchMutation, fetchAction, preloadQuery, api };

import {
  convexAuthNextjsMiddleware,
  createRouteMatcher,
} from "@convex-dev/auth/nextjs/server";

const isProtectedRoute = createRouteMatcher(["/protected(.*)"]);

// Named export for Next.js 16+ (proxy.ts)
export const proxy = convexAuthNextjsMiddleware((request, { convexAuth }) => {
  if (isProtectedRoute(request) && !convexAuth.isAuthenticated()) {
    return Response.redirect(new URL("/auth/login", request.url));
  }
});

export const config = {
  matcher: ["/((?!.*\\..*|_next).*)", "/", "/(api|trpc)(.*)"],
};
